package com.helloworld;

import com.facebook.react.ReactActivity;
import com.facebook.react.ReactActivityDelegate;
import com.facebook.react.ReactRootView;
import com.swmansion.gesturehandler.react.RNGestureHandlerEnabledRootView;

// 추가
import com.facebook.react.modules.core.PermissionListener; // 추가
import com.imagepicker.ImagePickerPackage; // 추가

public class MainActivity extends ReactActivity {

  /**
   * Returns the name of the main component registered from JavaScript. This is used to schedule
   * rendering of the component.
   */
  @Override
  protected String getMainComponentName() {
    return "HelloWorld";
  }

  /**
   * Returns the instance of the {@link ReactActivityDelegate}.
   * Here we use a util class {@link DefaultReactActivityDelegate} which allows you to easily
   * enable Fabric and Concurrent React (aka React 18) with two boolean flags.
   */
  @Override
  protected ReactActivityDelegate createReactActivityDelegate() {
    return new DefaultReactActivityDelegate(this, getMainComponentName()) {
      @Override
      protected ReactRootView createRootView() {
        return new RNGestureHandlerEnabledRootView(MainActivity.this);
      }

      // 추가
      @Override
      protected PermissionListener getPermissionListener() {
        return new ImagePickerPackage();
      }
    };
  }
}
